mapper.update:
UPDATE DeviceCategory
Name = #{entity.name}, VersionTag = #{entity.versionTag}, UpdateTime = now()
where Id = #{entity.id}

mapper.select:
select Id,Code,Name,VersionTag,IsPrimary from DeviceCategory 


		and IsPrimary = #{param.isPrimary}


		or Name like concat('%',#{param.globalName},'%') or Code like concat('%',#{param.globalName},'%')



    order by ${param.orderBy} asc


    order by ${param.orderBy} desc


    LIMIT #{param.startIndex}, #{param.pageSize}

select count(Code) from DeviceCategory


		and IsPrimary = #{param.isPrimary}


		or Name like concat('%',#{param.globalName},'%') or Code like concat('%',#{param.globalName},'%')

SELECT ASS.DeviceCategoryId, SMD.Id, SMD.TypeId, SMT.Name as TypeName, SMD.Name, SMD.Unit, SMD.DisplayMode as IsExtraDisplayMode FROM SpecModelData as SMD INNER JOIN SpecModelType as SMT ON SMD.TypeId = SMT.Id INNER JOIN DeviceCategory_SpecModelData_ASS as ASS ON SMD.Id = ASS.SpecModelDataId
ASS.DeviceCategoryId In
#{deviceCategoryId}
