mapper.select:
SELECT D.ProcessNo AS processNo, D.Code AS code, D.Name AS name, D.DeviceCategoryId AS deviceCategoryId, DC.Code AS deviceCategoryCode, DC.Name AS deviceCategoryName, D.Model AS model, D.LocationId AS locationId, L.Code AS locationCode, L.Name AS locationName, D.Status AS status, D.ParentId AS parentId FROM `Device` D LEFT JOIN `DeviceCategory` DC ON D.DeviceCategoryId = DC.Id LEFT JOIN `Location` L ON D.LocationId = L.Id
and D.ParentId = #{param.parentId} and D.IsPrimary = 0
and D.IsPrimary = 1
and DC.Code in
#{categoryId}
and L.Code in
#{locationId}
and D.Status in
#{statusId}
and ( D.ProcessNo like concat('%',#{param.globalName},'%') OR D.Code like concat('%',#{param.globalName},'%') OR D.Name like concat('%',#{param.globalName},'%') OR D.Model like concat('%',#{param.globalName},'%') OR D.Id in (SELECT DISTINCT DSA.DeviceId FROM `Device_SpecModelData_ASS` DSA INNER JOIN `SpecModelData` SMD ON DSA.SpecModelDataId = SMD.Id WHERE SMD.Name LIKE concat('%',#{param.globalName},'%') ) )
order by D.${param.orderBy} asc
order by D.${param.orderBy} desc
LIMIT #{param.startIndex}, #{param.pageSize}

SELECT COUNT(1) as count FROM `Device` D LEFT JOIN `DeviceCategory` DC ON D.DeviceCategoryId = DC.Id LEFT JOIN `Location` L ON D.LocationId = L.Id
and D.ParentId = #{param.parentId} and D.IsPrimary = 0
and D.IsPrimary = 1
and DC.Code in
#{categoryId}
and L.Code in
#{locationId}
and D.Status in
#{statusId}
and ( D.ProcessNo like concat('%',#{param.globalName},'%') OR D.Code like concat('%',#{param.globalName},'%') OR D.Name like concat('%',#{param.globalName},'%') OR D.Model like concat('%',#{param.globalName},'%') OR D.Id in (SELECT DISTINCT DSA.DeviceId FROM `Device_SpecModelData_ASS` DSA INNER JOIN `SpecModelData` SMD ON DSA.SpecModelDataId = SMD.Id WHERE SMD.Name LIKE concat('%',#{param.globalName},'%') ) )
